import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import GroupIcon from '@material-ui/icons/Group';
import ListAltIcon from '@material-ui/icons/ListAlt';
import LibraryBooksIcon from '@material-ui/icons/LibraryBooks';
import GroupWorkIcon from '@material-ui/icons/GroupWork';
import Button from '@material-ui/core/Button';

const drawerWidth = 240;

const Sidebar = (props) => {
  const classes = useStyles();
  const theme = useTheme();
  const location = useLocation();

  const {
    closeSidebar,
    open,
    showNotificationButton,
    askForNotificationPermission,
  } = props;

  return (
    <Drawer
      className={classes.drawer}
      variant='persistent'
      anchor='left'
      open={open}
      classes={{
        paper: classes.drawerPaper,
      }}
    >
      <div className={classes.drawerHeader}>
        <IconButton onClick={closeSidebar}>
          {theme.direction === 'ltr' ? (
            <ChevronLeftIcon className={classes.icon} />
          ) : (
            <ChevronRightIcon />
          )}
        </IconButton>
      </div>
      <Divider />
      <List>
        <NavLink to='/naracki' className={classes.link}>
          <ListItem button key={1} selected={location.pathname === '/naracki'}>
            <ListItemIcon className={classes.icon}>
              <ListAltIcon />
            </ListItemIcon>
            <ListItemText primary='Нарачки' />
          </ListItem>
        </NavLink>
        <Divider
          variant='middle'
          style={{
            backgroundColor: location.pathname === '/naracki' ? '#ccc' : null,
          }}
        />
        <NavLink to='/partneri' className={classes.link}>
          <ListItem button key={1} selected={location.pathname === '/partneri'}>
            <ListItemIcon className={classes.icon}>
              <GroupIcon />
            </ListItemIcon>
            <ListItemText primary='Партнери' />
          </ListItem>
        </NavLink>
        <Divider
          variant='middle'
          style={{
            backgroundColor: location.pathname === '/partneri' ? '#ccc' : null,
          }}
        />

        <NavLink to='/grupi' className={classes.link}>
          <ListItem button key={1} selected={location.pathname === '/grupi'}>
            <ListItemIcon className={classes.icon}>
              <GroupWorkIcon />
            </ListItemIcon>
            <ListItemText primary='Групи' />
          </ListItem>
        </NavLink>
        <Divider
          variant='middle'
          style={{
            backgroundColor: location.pathname === '/grupi' ? '#ccc' : null,
          }}
        />

        <NavLink to='/artikli' className={classes.link}>
          <ListItem button key={1} selected={location.pathname === '/artikli'}>
            <ListItemIcon className={classes.icon}>
              <LibraryBooksIcon />
            </ListItemIcon>
            <ListItemText primary='Артикли' />
          </ListItem>
        </NavLink>
        <Divider
          variant='middle'
          style={{
            backgroundColor: location.pathname === '/artikli' ? '#ccc' : null,
          }}
        />
      </List>
      {showNotificationButton && (
        <div className={classes.btnContainer}>
          <Button
            style={{ backgroundColor: 'white' }}
            color='primary'
            type='button'
            fullWidth
            onClick={askForNotificationPermission}
          >
            Enable notifications
          </Button>
        </div>
      )}
    </Drawer>
  );
};

const useStyles = makeStyles((theme) => ({
  menuButton: {
    marginRight: theme.spacing(2),
  },
  hide: {
    display: 'none',
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
  },
  drawerPaper: {
    width: drawerWidth,
    background: theme.palette.primary.main,
    opacity: 0.9,
  },
  drawerHeader: {
    display: 'flex',
    alignItems: 'center',
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
    justifyContent: 'flex-end',
  },
  link: {
    textDecoration: 'none',
    color: theme.palette.primary.contrastText,
  },
  icon: {
    color: theme.palette.primary.contrastText,
  },
  btnContainer: {
    // marginTop: 5,
    // marginRight: 15,
    // marginLeft: 15,
  },
}));

export default Sidebar;
